// Class Based comp.


import React, {Component} from 'react';
import About from './About';

class DemoComponent extends Component{

    constructor(props){
        super(props)
        this.state = {
            toggle: false,
            name: 'dimple'

        }

    }

    changeToggle = () => {
        this.setState({
            toggle: !this.state.toggle
        })
    }

    render(){
        return(
            <div>
                <button onClick={this.changeToggle} style={{color: this.state.toggle ? 'red' : 'green'}}>Toggle</button>
                This is a demo component
                <About
                    name={this.state.name}
                />
                {
                    this.state.toggle ? <h1>Toggle On</h1> : <h1>Toggle Off</h1>
                }

            </div>
        )
    }

}

export default DemoComponent
