import React, { useState, useEffect } from 'react';

const Demo = (props) => {

    const [name, setName] = useState('Dimple Sharma')
    const [age, setAge] = useState(null)

    useEffect(()=>{
        setAge(25)
    }, [])

    const demoFun =  () => {
        setName('Abhishek')
    }

    const updateParent = () => {
        props.UpdateParent('argument')
    }


    return (
        <div>
            <p> Parent ----------- { props.temp } </p>
            <button onClick={updateParent}>Child Click</button>
            Hello this is demo { name }
            <p>
                <button onClick={demoFun}>
                    Change Name
                </button>
            </p>

        </div>
    )


}

export default Demo
